package org.corejava;

public class ConstructorPractice {
	int a ;
	int b;
	
	public ConstructorPractice(int a, int b) {
		// TODO Auto-generated constructor stub
		this.a=a;
		this.b=b;
			}
	
	 int sumOfNumber() {
		// TODO Auto-generated method stub
		return a+b;

	}
	 int subtractOfNumber() {
			// TODO Auto-generated method stub
			return b-a;

		}
	 

}
