package org.corejava;

public class EncapsulationPractice {
	private int a;
	private int b;
	
	public void set(int a) {
		this.a=a;
	}
	public int get() {
		return a;
	}
	
	public void setB(int b) {
		this.b=b;
	}
	public int getB() {
		return b;
	}
	
	public int sum() {
		// TODO Auto-generated method stub
		return a+b;
	}
	

}
